import React, {setState, event} from 'react'
import Flashcard from "./Flashcard";
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
// import GridListTile from '@material-ui/core/GridListTile';

const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      backgroundColor: theme.palette.background.paper,
    },
    gridList: {
      width: 500,
      height: 450,
      // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
      transform: 'translateZ(0)',
    },
    titleBar: {
      background:
        'linear-gradient(to bottom, rgba(0,0,0,0.7) 0%, ' +
        'rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
    },
    icon: {
      color: 'white',
    },
  }));

//   filter(e){
//     this.setState({filter: e.target.value})
// }



export default function FlashcardList( { flashcards } ) {

    // const [filter, filtered] = setState(event.target.value)

    const classes = useStyles();

    let items = flashcards;
    // if (filter){
    //     items = items.filter( item =>
    //         item.id.toLowerCase()
    //         .includes.this.state.filter.toLowerCase)
    // }

    // editSearchTerm = (e) => {
    //     this.setState({searchTerm: e.target.value})
    //   }

    // dynamicSearch = () => {
    //     return flashcards.filter(name => name.toLowerCase().includes(this.state.searchTerm.toLowerCase()))
    //   }


    return (



        <div className={classes.root}>

        {/* <input type="text" onChange={this.filtered.bind(this)}/> */}

        {/* <GridList cellHeight={100} spacing={4} className={classes.gridList}> */}
        <Grid container spacing={3}>

        <div className = "card-grid">
            <Grid item xs>
            {items.map(flashcard => {
                return <Flashcard flashcard = {flashcard} key = {flashcard.id}> </Flashcard>
            })}
            </Grid>

        </div>
        {/* </GridList> */}
        </Grid>

    </div>

    )
}
