// import React, {setState, event} from 'react'

import React, {useState, useEffect } from 'react'
import Button from "@material-ui/core/Button";
import PauseCircleFilled from '@material-ui/icons/PauseCircleFilled';
import PlayCircleFilled from '@material-ui/icons/PlayCircleFilled';
import CachedIcon from '@material-ui/icons/Cached';
import Slider from '@material-ui/core/Slider';
import useAudioPlayer from './useAudioPlayer';


  function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min; 
  }

  function createQuiz(items, num) {
    var i; var songList = [];
    if (num > items.length) {
      return;
    }
    for (i = 0; i < num; i++){
      songList.push(items[getRandomInt(0, items.length)]);
    }
    return songList;
  }



//   filter(e){
//     this.setState({filter: e.target.value});
// }




export default function Quiz( { flashcards } ) {

    var audio = new Audio();
    // const { curTime, duration, playing, setPlaying, setClickedTime } = useAudioPlayer(audio);
    const [playing, setPlaying] = useState(true);


    function makeSongList(audio, songList) {

        audio.preload = "auto";
        audio.controls = true;
        var i = 0;
        playing ? console.log('1play') : console.log('1paue');

        audio.addEventListener('ended', function () {

            i++;
            setTrope(i);
            if ((i < songList.length)){
                audio.src = songList[i].sound;
                audio.playbackRate = 2;
                playing ? audio.play() : audio.pause();
                playing ? console.log('plaaaaay') : console.log('pauaaaase');
                // audio.play();
            }

          }, true);

            audio.volume = 0.3;
            // audio.loop = false;
            setTrope(0);
            audio.src = songList[0].sound;
            audio.playbackRate = 2;
            playing ? audio.play() : audio.pause();
            playing ? console.log('plaay') : console.log('pauase');


            // audio.play();
    }
    // const [filter, filtered] = setState(event.target.value)
    let items = flashcards;

    const [quiz, reloadQuiz] = useState(createQuiz(items, getRandomInt(2, 10)));
    const [trope, setTrope] = React.useState(0);

    //pick a song
    // const handleSong = (event, newTrope) => {
    //     setTrope(newTrope);
    // };

    const [value, setValue] = React.useState(10);
    const handleChange = (event, newValue) => {
        setValue(newValue);
        reloadQuiz(createQuiz(items, newValue))
    };

    // useEffect(() => {
    //     // const audio = document.getElementById("audio");
    //     playing ? audio.play() : audio.pause();
    //     playing ? console.log("hi") : console.log("bye");

    // })





    // var quiz = createQuiz(items, 4);
    // if (filter){
    //     items = items.filter( item =>
    //         item.id.toLowerCase()
    //         .includes.this.state.filter.toLowerCase)
    // }

    // editSearchTerm = (e) => {
    //     this.setState({searchTerm: e.target.value})
    //   }

      // reloadQuiz = (e) => {
      //   this.setState({quiz: createQuiz(items, 4)})
      // }
    // dynamicSearch = () => {
    //     return flashcards.filter(name => name.toLowerCase().includes(this.state.searchTerm.toLowerCase()))
    //   }


    return (
         <quiz>
            <h5> Practice
           {/* {pause === true ? 'paused' : 'playing'} */}

           <Button size="small" variant="contained"> 
           <Slider value={value} onChange={handleChange} defaultValue={4}
                // position="static"
                step={1} marks min={1} max={8} valueLabelDisplay="auto" />

            </Button>


            <div className="controls">

                <Button variant="contained" color="primary"
                    endIcon={<CachedIcon  onClick={() =>  {reloadQuiz(createQuiz(items, value)); setPlaying(true); console.log("hit play");}}/>}>
                </Button> 

                {playing ?  
                    <Button variant="contained" color="primary" 
                        endIcon={<PauseCircleFilled onClick={() => { setPlaying(!playing); console.log("hit pause");}}/>}>
                        {playing ? 'play' : 'pause'}
                    </Button> :
                    <Button variant="contained" color="primary" 
                        endIcon={<PlayCircleFilled onClick={() => {makeSongList(audio, quiz); setPlaying(!playing); console.log("hit play");}}/>}>
                        {playing ? 'play' : 'pause'}
                    </Button>
                }

                {/* <Bar curTime={curTime} duration={duration} onTimeUpdate={(time) => setClickedTime(time)}/> */}
            </div>
           {/* <Button variant="contained" color="primary"
                endIcon={<CachedIcon  onClick={() =>  {reloadQuiz(createQuiz(items, value)); setPlaying(true);}}/>}>
            </Button> */}


            {/* <Button variant="contained" color="primary" 
                endIcon={<PlayCircleFilled onClick={() => {makeSongList(audio, quiz);  setPlaying(false);}}/>}>
            </Button> */}
{/*             
            <Button variant="contained" color="primary" 
                endIcon={<PauseCircleFilled onClick={() => { setPlaying(true);}}/>}>
            </Button> */}

            </h5>


        <p>
        {quiz.map((songName, index)=> {return ( 
           <i style={(trope === index) ? {color: 'black'} : {color: 'yellow'}}>{songName.heb}</i>)})}
        </p>


           </quiz>

    )
}
