import React, {useState} from 'react'
import Button from "@material-ui/core/Button";
import PlayCircleOutlineIcon from '@material-ui/icons/PlayCircleOutline';

import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';


const playSound = audioFile => {
    audioFile.play();
  };

const useStyles = makeStyles({
    root: {
      maxWidth: 345
    },
    },
    );

export default function Flashcard( {flashcard }) {
    const [flip, setFlip] = useState(false)
    const song =  new Audio(flashcard.sound)
    const classes = useStyles();

    return (

        // < div onClick={() => setFlip(!flip)} >


        <Card className={classes.root} color="blue">
            <CardActionArea onClick={() => playSound(song)}>
            <CardContent>
          <Typography gutterBottom variant="h5" color="black" component="h2">
          <div className = "back">
             <img src = {flashcard.image}></img>
             <div>{flashcard.heb}</div>

            </div>          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
          {flashcard.eng}
          </Typography>
        </CardContent>
        {/* {flip ? <img src = {flashcard.image}></img> : (flashcard.eng + ' ' + flashcard.heb)} */}
            <Button
                variant="contained"
                color="primary"
                endIcon={
                    <PlayCircleOutlineIcon onClick={() => {playSound(song)}}>
                    </PlayCircleOutlineIcon>
                }
            >
                Play
            </Button>
      </CardActionArea>
      </Card>

            // {/* <div className = "front"> */}
            //     {/* {flashcard.question} */}

            //     {/* <div className = "card-options">  */}
            //         {/* {
            //             flashcard.options.map(option => {
            //                 return <div className="flashcard-option">{option}</div>
            //             })
            //         }  </div> */}


            // {/* </div> */}




    )
}
