  const [value, setValue] = React.useState(0);
  const [flashcards, setFlashcard] = useState(Samples);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (

    <BrowserRouter>
    <div className="App">
    <Countdown date={'2020-09-05T09:00:00'} text='Ki Tavo Bar Mitzva Countdown' />

      <Route
        path="/"
        render={({ location }) => (
          <Fragment variant="dense">
            <HideOnScroll {...props}>

            <navMe>
                <Tabs value={location.pathname} indicatorColor="primary" textColor="primary" centered > 
                <Tab label="Tikkun" component={Link} to="/Tikkun" />
                <Tab label="Haftorah" component={Link} to="/Haftorah" />
                <Tab
                  label="Tropes"
                  href="#basic-tabs"
                  component={Link}
                  to="/Tropes"
                />
              </Tabs>
            <ScrollTop {...props}>
                 <Fab color="secondary" size="small" aria-label="scroll back to top">
                   <KeyboardArrowUpIcon />
                 </Fab>
               </ScrollTop>
            </navMe>
            </HideOnScroll>

            <Switch>
              <Route path="/Tikkun" render={() => <div>
                <Player />
                <div class= "master_tikkun">
                  <img src={master_tikkun} alt="master_tikkun" loading="lazy" width="2000" height="1000"/>
                </div>


                {/* <AppWorkin></AppWorkin> */}
                </div>} />
              <Route path="/Haftorah" render={() => <div> 
                <embed src={haftorah} type="application/pdf"   height="800px" width="500"/>
                <embed src={torah} type="application/pdf"   height="800px" width="500"/>

                 </div>} />
              <Route path="/Tropes" render={() => <div> 
                <Ropes></Ropes> 

                </div>} />
              <Route path="/" render={() => <div>
                MASTER SLIDE
                <img src={bm} alt="bm" loading="lazy" width="500" height="500"/>
                {/* <div class= "master_tikkun">
                  <img src={master_tikkun} alt="master_tikkun" loading="lazy" width="2000" height="1000"/>
                </div> */}
              </div>} />
            </Switch>
          </Fragment>
