import React from 'react';
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import Box from '@material-ui/core/Box';
import Container from '@material-ui/core/Container';
import Fab from '@material-ui/core/Fab';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import Zoom from '@material-ui/core/Zoom';
import AppWorkin from './AppWorkin'
import Slide from '@material-ui/core/Slide';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Player from './Player'

import FlashcardList from "./FlashcardList";
import {Samples} from './Tropes'
import { useState }  from 'react'; 
import Countdown from './countdown'; // /** This countdown component is from https://medium.com/@kristin_baumann/react-countdown-6455838b6faf */

const useStyles = makeStyles((theme) => ({
  root: {
    // position: 'fixed',
    bottom: theme.spacing(2),
    right: theme.spacing(2),
  },
}));


function HideOnScroll(props) {
  const { children, window } = props;
  // Note that you normally won't need to set the window ref as useScrollTrigger
  // will default to window.
  // This is only being set here because the demo is in an iframe.
  const trigger = useScrollTrigger({ target: window ? window() : undefined });

  return (
    <Slide appear={false} direction="down" in={!trigger}>
      {children}
    </Slide>
  );
}

HideOnScroll.propTypes = {
  children: PropTypes.element.isRequired,
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
  window: PropTypes.func,
};

function ScrollTop(props) {
  const { children, window } = props;
  const classes = useStyles();
  // Note that you normally won't need to set the window ref as useScrollTrigger
  // will default to window.
  // This is only being set here because the demo is in an iframe.
  const trigger = useScrollTrigger({
    target: window ? window() : undefined,
    disableHysteresis: true,
    threshold: 100,
  });

  const handleClick = (event) => {
    const anchor = (event.target.ownerDocument || document).querySelector('#back-to-top-anchor');

    if (anchor) {
      anchor.scrollIntoView({ behavior: 'smooth', block: 'center' });
    }
  };

  return (
    <Zoom in={trigger}>
      <div onClick={handleClick} role="presentation" className={classes.root}>
        {children}
      </div>
    </Zoom>
  );
}

ScrollTop.propTypes = {
  children: PropTypes.element.isRequired,
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
  window: PropTypes.func,
};

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

function TabContainer(props) {
  return (
    <Typography component="div" style={{ padding: 8 * 3 }}>
      {props.children}
    </Typography>
  );
}

// TabPanel.propTypes = {
//   children: PropTypes.node,
//   index: PropTypes.any.isRequired,
//   value: PropTypes.any.isRequired,
// };

// function a11yProps(index) {
//   return {
//     id: `simple-tab-${index}`,
//     'aria-controls': `simple-tabpanel-${index}`,
//   };
// }

export default function BackToTop(props) {

  const [value, setValue] = React.useState(0);
  const [flashcards, setFlashcard] = useState(Samples);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <React.Fragment>
      <CssBaseline />
      <HideOnScroll {...props}>

        <AppBar>
        <Countdown date={'2020-09-05T09:00:00'} text='Ki Tavo Bar Mitzva Countdown' />


        <Tabs value={value} onChange={handleChange} aria-label="simple tabs example">
          {/* <Tab label="Item One" {...a11yProps(0) />
          <Tab label="Item Two" {...a11yProps(1)} />
          <Tab label="Item Three" {...a11yProps(2)} /> */}
          <Tab label="Item One"/>
          <Tab label="Item Two" />
          <Tab label="Item Three" /> 
        </Tabs>

        {/* <Toolbar>
          <Typography variant="h6">Scroll to see button</Typography>
        </Toolbar> */}
      {/* <TabPanel value={value} index={0}>
        <AppWorkin></AppWorkin>
      </TabPanel>
      <TabPanel value={value} index={1}>
        <FlashcardList flashcards = {flashcards} />
      </TabPanel>
      <TabPanel value={value} index={2}>
        Item Three
      </TabPanel> */}

      {value === 0 && <TabContainer>

          Item One
          <AppWorkin></AppWorkin>
        </TabContainer>}
      {value === 1 && <TabContainer>Item One</TabContainer>}
      {value === 2 && <TabContainer> <player/> </TabContainer>}


      </AppBar>
      </HideOnScroll>

        <player></player>
      <Toolbar id="back-to-top-anchor" />
      {/* <AppWorkin></AppWorkin> */}
      <ScrollTop {...props}>
        <Fab color="secondary" size="small" aria-label="scroll back to top">
          <KeyboardArrowUpIcon />
        </Fab>
      </ScrollTop>

    </React.Fragment>
  );
}
