import FlashcardList from "./FlashcardList";
import {Samples} from './Tropes'
import { useState }  from 'react'; 
import Countdown from './countdown'; // /** This countdown component is from https://medium.com/@kristin_baumann/react-countdown-6455838b6faf */

import master_tikkun from './tikkun-master4.png'; // Tell webpack this JS file uses this image
import bm from './barmit.png'; // Tell webpack this JS file uses this image
import haftorah from './SS_Haftorah.pdf'; // Tell webpack this JS file uses this image
import torah from './SS_Torah.pdf'; // Tell webpack this JS file uses this image
import master from './Master';
import pdfs from './pdfs';

const useStyles = makeStyles((theme) => ({
  root: {
    position: 'fixed',
    bottom: theme.spacing(2),
    right: theme.spacing(2),
  },
}));

export default function BackToTop(props) {

  const [value, setValue] = React.useState(0);
  const [flashcards, setFlashcard] = useState(Samples);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (

    <BrowserRouter>
    <div className="App">
    <Countdown date={'2020-09-05T09:00:00'} text='Ki Tavo Bar Mitzva Countdown' />

      <Route
        path="/"
        render={({ location }) => (
          <Fragment variant="dense">

                <Tabs value={location.pathname} indicatorColor="primary" textColor="primary" centered > 

                <Tooltip title="Practice Torah Reading" arrow placement="left">
                  <Tab label="Tikkun" component={Link} to="/Tikkun" />
                </Tooltip>
                <Tab label="Torah & Haftorah Blessings" component={Link} to="/Haftorah" />
                <Tooltip title="Quiz yourself on the Tropes" arrow placement="right">
                <Tab
                  label="Tropes"
                  component={Link}
                  to="/Tropes"
                />
                </Tooltip>
              </Tabs>
            <Switch>
              <Route path="/Tikkun" render={() => <div>
                <Player />
                <div class= "master_tikkun">
                  <img src={master_tikkun} alt="master_tikkun" loading="lazy" width="2000" height="1000"/>
                </div>
                </div>} />
              <Route path="/Haftorah" render={() => <div>
                <pdfs></pdfs>
{/*  
                <embed src={haftorah} type="application/pdf" height="800px" width="800px"/>
                <embed src={torah} type="application/pdf" height="800px" width="800px"/>  */}

                 </div>} />
              <Route path="/Tropes" render={() => <div> <Ropes></Ropes> </div>} />
              <Route path="/" render={() => <div>
                MASTER SLIDE
                <Master/>
              </div>} />
            </Switch>
          </Fragment>
        )}
      />
    </div>
  </BrowserRouter>

    // <BrowserRouter>

    //     <AppBar position="static" color="default">
    //         <Tabs>
    //           {/* <Typography variant="h6" style={{ flexGrow: 1, whiteSpace: "nowrap" }}>
    //             <Link underline="none" color="inherit" component={RouterLink} to="/player">
    //               Set with Friends
    //             </Link>
    //           </Typography> */}
    //           <Tab label="Item One" component={Link} to="/player" />
    //           {/* <Route exact path="/player" component={Player} /> */}
    //           <Tab label="Item Two" component={Link} to="/" />
    //           {/* <Route exact path="/" component={Ropes} /> */}

    //         </Tabs>
    //       </AppBar>
    //   <Switch>
    //     <Route exact path="/player" component={Player} />
    //     <Route exact path="/" component={Ropes} />
    //   </Switch>
    // </BrowserRouter>


    // <BrowserRouter>
    //       <AppBar position="static" color="default">
    //       <Tabs>
    //           <Tab label="Item One" component={Link} to="/one" />
    //           <Tab label="Item Two" component={Link} to="/two" />
    //         </Tabs>
    //       </AppBar>

    //       <Switch>
    //         <Route path="/one" component={(ItemOne)} />
    //         <Route path="/two" component={(ItemTwo)} />
    //       </Switch>
    //   </BrowserRouter>

    // <React.Fragment>
    //   <CssBaseline />

    //   <HideOnScroll {...props}>

    //     <AppBar>
    //     <Countdown date={'2020-09-05T09:00:00'} text='Ki Tavo Bar Mitzva Countdown' />


    //     <Tabs value={value} onChange={handleChange} aria-label="simple tabs example">
    //       {/* <Tab label="Item One" {...a11yProps(0) />
    //       <Tab label="Item Two" {...a11yProps(1)} />
    //       <Tab label="Item Three" {...a11yProps(2)} /> */}
    //       <Tab label="Item One"/>
    //       <Tab label="Item Two" />
    //       <Tab label="Item Three" /> 

    //     </Tabs>

    //   {value === 0 && <TabContainer>

    //       Item One
    //       <AppWorkin></AppWorkin>
    //     </TabContainer>}
    //   {value === 1 && <TabContainer>Item One</TabContainer>}
    //   {value === 2 && <TabContainer> <player></player></TabContainer>}


    //   </AppBar>
    //   </HideOnScroll>


    //   <Toolbar id="back-to-top-anchor" />
    //   {/* <AppWorkin></AppWorkin> */}
    //   <ScrollTop {...props}>
    //     <Fab color="secondary" size="small" aria-label="scroll back to top">
    //       <KeyboardArrowUpIcon />
    //     </Fab>
    //   </ScrollTop>

    // </React.Fragment>
  );
}
