// import React, {setState, event} from 'react'

import React, {useState} from 'react'
import Flashcard from "./Flashcard";
import { makeStyles } from '@material-ui/core/styles';
import PlayArrowIcon from '@material-ui/icons/PlayArrow';
import Button from "@material-ui/core/Button";
import PlayCircleOutlineIcon from '@material-ui/icons/PlayCircleOutline';
import PauseCircleOutlineIcon from '@material-ui/icons/PauseCircleOutline';
import CachedIcon from '@material-ui/icons/Cached';
import MobileStepper from '@material-ui/core/MobileStepper';
import KeyboardArrowLeft from '@material-ui/icons/KeyboardArrowLeft';
import KeyboardArrowRight from '@material-ui/icons/KeyboardArrowRight';
import Slider from '@material-ui/core/Slider';
import { render } from 'react-dom';


const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
    },
    grid: {
      width: 500,
      height: 450,
      direction: "row",
      justify: "center",
      alignItems: "center",
    },
  }));

  function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min; 
  }

  function createQuiz(items, num) {
    var i; var songList = [];
    if (num > items.length) {
      return;
    }
    for (i = 0; i < num; i++){
      songList.push(items[getRandomInt(0, items.length)]);
    }
    return songList;
  }



//   filter(e){
//     this.setState({filter: e.target.value});
// }

function valuetext(value) {
    return `${value}`;
  }


export default function Quiz( { flashcards } ) {

    function makeSongList(songList) {

        var audio = new Audio();
        audio.preload = "auto";
        audio.controls = true;
        var i = 0;

        audio.addEventListener('ended', function () {

            i++;
            setTrope(i);
            if ((i < songList.length)){
                audio.src = songList[i].sound;      
                audio.playbackRate = 2;
                audio.play();}

          }, true);

            audio.volume = 0.3;
            // audio.loop = false;
            setTrope(0);
            audio.src = songList[0].sound;
            audio.playbackRate = 2;
            audio.play();
    }
    // const [filter, filtered] = setState(event.target.value)
    let items = flashcards;

    const [quiz, reloadQuiz] = useState(createQuiz(items, getRandomInt(2, 10)));

    const [pause, setPause] = useState(false)

    //pick a song
    const [trope, setTrope] = React.useState(0);
    const handleSong = (event, newTrope) => {
        setTrope(newTrope);
    };

    const [value, setValue] = React.useState(10);
    const handleChange = (event, newValue) => {
        setValue(newValue);
        reloadQuiz(createQuiz(items, newValue))
    };

    var x;

    var audioElement= document.getElementById("audio-player");
    function togglePlay() {
        if (audioElement.paused) {
            audioElement.play();
        }
        else {
            audioElement.pause();
        }
    };

    // var quiz = createQuiz(items, 4);
    // if (filter){
    //     items = items.filter( item =>
    //         item.id.toLowerCase()
    //         .includes.this.state.filter.toLowerCase)
    // }

    // editSearchTerm = (e) => {
    //     this.setState({searchTerm: e.target.value})
    //   }

      // reloadQuiz = (e) => {
      //   this.setState({quiz: createQuiz(items, 4)})
      // }
    // dynamicSearch = () => {
    //     return flashcards.filter(name => name.toLowerCase().includes(this.state.searchTerm.toLowerCase()))
    //   }


    return (
         <quiz>

           <p>
           {pause === true ? 'paused' : 'playing'}

           <Button size="small" variant="contained"> 
           <Slider value={value} onChange={handleChange} defaultValue={4}
                // position="static"
                step={1} marks min={1} max={8} valueLabelDisplay="auto" />

            </Button>

           <Button variant="contained" color="primary"
                endIcon={<CachedIcon  onClick={() =>  {reloadQuiz(createQuiz(items, value)); setPause(true);}}/>}>
            </Button>


            <Button variant="contained" color="primary" 
                endIcon={<PlayCircleOutlineIcon onClick={() => {makeSongList(quiz); setPause(false);}}/>}>
            </Button>

            <Button variant="contained" color="primary" 
                endIcon={<PauseCircleOutlineIcon onClick={() => {setPause(true)}}/>}>
            </Button>


            </p>


        <p>
        {quiz.map((songName, index)=> {return ( 
           <i style={(trope === index) ? {color: 'black'} : {color: 'yellow'}}>{songName.heb}</i>)})}
        </p>


           </quiz>

    )
}
