// import React, {setState, event} from 'react'

import React, {useState, useEffect } from 'react'
import Button from "@material-ui/core/Button";
import PauseCircleFilled from '@material-ui/icons/PauseCircleFilled';
import PlayCircleFilled from '@material-ui/icons/PlayCircleFilled';
import CachedIcon from '@material-ui/icons/Cached';
import Slider from '@material-ui/core/Slider';

  function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min; 
  }

  function createQuiz(items, num) {
    var i; var songList = [];
    if (num > items.length) {
      return;
    }
    for (i = 0; i < num; i++){
      songList.push(items[getRandomInt(0, items.length)]);
    }
    return songList;
  }


export default function Quiz( { flashcards } ) {

    var audio = new Audio();
    let jo = 0;
    audio.addEventListener('loadeddata', () => {
        jo = audio.duration;
        // The duration variable now holds the duration (in seconds) of the audio clip 
      })

    const [flag, setFlag] = useState(1);
    const [trope, setTrope] = useState(0);

    let i = -1;
    let myflag = 0;

    function playNext (){
        // if (!myflag) printc("JACKSA");
        // var i = trope;
        ++i;
        if (i < quiz.length){
            audio.preload = "auto";
            // audio.volume = 0.3;
            audio.src = quiz[i].sound;
            audio.playbackRate = 2;
            audio.play();
            audio.onended = function(){
                audio.remove() //Remove when played.
              };
        }
        else{ 
            setFlag(1);
            i = -1;
        }
        setTrope(i);


    }
    audio.addEventListener('ended', function () {
        printc("song ended");
        if (!myflag*flag) playNext();
    }, false);

    let items = flashcards;

    const [quiz, reloadQuiz] = useState(createQuiz(items, getRandomInt(2, 10)));

    const [value, setValue] = React.useState(10);

    const handleChange = (event, newValue) => {
        setValue(newValue);
        reloadQuiz(createQuiz(items, newValue))
    };

    function printc(calledBy){
        console.log(calledBy, "i= ", i, "myflag= ", myflag);

        // console.log(calledBy, "i= ", i, "myflag= ", myflag, " trope=", trope, "/", quiz.length, " flag=", flag);
    }

    function handlePlay(){
        myflag = 0;
        setTrope(0);
        printc("handlePlay b4 PlayNext");
        playNext();
        printc("handlePlay Aftr PlayNext");
        setFlag(0); 

        // makeSongList(quiz);
    }

    function handlePause(){
        myflag = 1;
        audio.pause();
        printc("handlePause after setFlag");
        setFlag(1);
        i = 0;
        setTrope(0);


    }




    // var quiz = createQuiz(items, 4);

    return (
         <quiz>
            <h5> 
            <div className="controls">


                <Button variant="contained" color="primary"
                    endIcon={<CachedIcon  onClick={() =>  {reloadQuiz(createQuiz(items, value));  console.log("ReOrder");}}/>}>
                </Button> 

                <Button size="small" variant="contained"> 
                    <Slider value={value} onChange={handleChange} defaultValue={4}
                            step={1} marks min={1} max={8} valueLabelDisplay="auto" />
                </Button>

                {flag ?  <Button variant="contained" color="primary" 
                    endIcon={<PlayCircleFilled onClick={() => {handlePlay()}}/>}>
                        P
                    </Button> :
                    <Button variant="contained" color="primary" 
                        endIcon={<PauseCircleFilled onClick={() => {handlePause()}}/>}>
                        S
                    </Button>
                }
                {myflag}{flag}{trope}{jo}
            </div>

            </h5>


        <p>
        {quiz.map((songName, index)=> {return ( 
           <i style={(trope === index) ? {color: 'black'} : {color: 'yellow'}}>{songName.heb}</i>)})}
        </p>

           </quiz>

    )
}
