// import React, {setState, event} from 'react'

import React, {useState} from 'react'
import Flashcard from "./Flashcard";
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import PlayArrowIcon from '@material-ui/icons/PlayArrow';
import Button from "@material-ui/core/Button";
import PlayCircleOutlineIcon from '@material-ui/icons/PlayCircleOutline';
import MobileStepper from '@material-ui/core/MobileStepper';
import KeyboardArrowLeft from '@material-ui/icons/KeyboardArrowLeft';
import KeyboardArrowRight from '@material-ui/icons/KeyboardArrowRight';


const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
    },
    grid: {
      width: 500,
      height: 450,
      direction: "row",
      justify: "center",
      alignItems: "center",
    },
  }));

  // const playSound = audioFile => {
  //   audioFile.play();
  // };
  // const song =  new Audio(flashcard.sound)

  function playMe(song) {
    // return new Audio(song);
    new Audio(song).play();
  }

  function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min; //The maximum is exclusive and the minimum is inclusive
  }

  function createQuiz(items, num) {
    var i; var songList = [];
    if (num > items.length) {
      return;
    }
    for (i = 0; i < num; i++){
      songList.push(items[getRandomInt(0, items.length)]);
    }
    return songList;
  }

  function makeSongList(songList) {

    var audio = new Audio();
    var i = 0;

    audio.addEventListener('ended', function () {

      // i = ++i < songList.length ? i : 0;
      i++;
      if (i < songList.length){
        console.log(i)
        audio.src = songList[i].sound;
        audio.play();}
      }, true);

    audio.volume = 0.3;
    // audio.loop = false;
    audio.src = songList[0].sound;
    audio.play();
  }

//   filter(e){
//     this.setState({filter: e.target.value});
// }



export default function FlashcardList( { flashcards } ) {

    // const [filter, filtered] = setState(event.target.value)
    let items = flashcards;

    // const [val, setNumQuizItems] = useState(3);
    const [quiz, reloadQuiz] = useState(createQuiz(items, getRandomInt(0, 10)));

    // const [activeStep, setActiveStep] = React.useState(0);

    // const handleNext = () => {
    //   setActiveStep((prevActiveStep) => prevActiveStep + 1);
    // };

    // const handleBack = () => {
    //   setActiveStep((prevActiveStep) => prevActiveStep - 1);
    // };

    const classes = useStyles();
    var x;

    // var quiz = createQuiz(items, 4);
    // if (filter){
    //     items = items.filter( item =>
    //         item.id.toLowerCase()
    //         .includes.this.state.filter.toLowerCase)
    // }

    // editSearchTerm = (e) => {
    //     this.setState({searchTerm: e.target.value})
    //   }

      // reloadQuiz = (e) => {
      //   this.setState({quiz: createQuiz(items, 4)})
      // }
    // dynamicSearch = () => {
    //     return flashcards.filter(name => name.toLowerCase().includes(this.state.searchTerm.toLowerCase()))
    //   }


    return (


    <div>
         <div className={classes.root}>

           <p>

           {/* <MobileStepper
            // variant=""
            steps={6}
            position="static"
            activeStep={activeStep}
            // className={classes.root}
            nextButton={
              <Button size="small" onClick={handleNext} disabled={activeStep === 5}>  
                Next <KeyboardArrowRight />
              </Button>
            }
            backButton={
              <Button size="small" onClick={handleBack} disabled={activeStep === 0}> <KeyboardArrowLeft />
                Back
              </Button>
            }/> */}

           <Button
                variant="contained"
                color="primary"
                endIcon={
                    <PlayCircleOutlineIcon onClick={() => {reloadQuiz(createQuiz(items, getRandomInt(0, 10)))}}>
                    </PlayCircleOutlineIcon>
                }>
                Reload 
            </Button>

            <Button
                variant="contained"
                color="primary"
                endIcon={
                    <PlayCircleOutlineIcon onClick={() => {makeSongList(quiz)}}>
                    </PlayCircleOutlineIcon>
                }>
            </Button>

           {quiz.map( songName => {return (<> {songName.heb} </>)})}
            {/* {makeSongList(quiz)} */}
           </p>
        {/* <input type="text" onChange={this.filtered.bind(this)}/> */}

      <Grid container spacing={3}> 
      {items.map(flashcard => {
                  return (
                  <Grid item xs={3} >
                    <Flashcard flashcard = {flashcard} key = {flashcard.id}> </Flashcard>
                  </Grid>
                  )
        })}

      </Grid>

    </div>
    </div>


    )
}
