// import React, {setState, event} from 'react'

import React, {useState, useEffect } from 'react'
import Button from "@material-ui/core/Button";
import PauseCircleFilled from '@material-ui/icons/PauseCircleFilled';
import PlayCircleFilled from '@material-ui/icons/PlayCircleFilled';
import CachedIcon from '@material-ui/icons/Cached';
import Slider from '@material-ui/core/Slider';
import useAudioPlayer from './useAudioPlayer';


  function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min; 
  }

  function createQuiz(items, num) {
    var i; var songList = [];
    if (num > items.length) {
      return;
    }
    for (i = 0; i < num; i++){
      songList.push(items[getRandomInt(0, items.length)]);
    }
    return songList;
  }



//   filter(e){
//     this.setState({filter: e.target.value});
// }




export default function Quiz( { flashcards } ) {

    var audio = new Audio();
    // const { curTime, duration, paused, setPause, setClickedTime } = useAudioPlayer(audio);
    const [paused, setPause] = useState(false);

    function makeSongList(audio, songList) {

        // audio.preload = "auto";
        audio.controls = true;
        var i = 0;
        // paused ? console.log('1play') : console.log('1paue');
        audio.addEventListener('pause', function () { 
            console.log("SOMEONE PAUSED ME!");
            if (paused === true) {
                console.log("why did they do it!?");}
        }, true);

        audio.addEventListener('ended', function () {

            i++;
            setTrope(i);

            console.log("hit end of audio ", i-1);
            // paused ? console.log('end - play TRUE') : console.log('end - play FALSE');

            // paused ? console.log('1play') : console.log('1paue');
            if ((i < songList.length) && (paused === false)){
                console.log("i < songLen and NOT paused" + i);
                audio.src = songList[i].sound;
                audio.playbackRate = 2;
                // paused ? audio.play() : audio.pause();
                // paused ? console.log('plaaaaay') : console.log('pauaaaase');
                if (paused === false) {
                    console.log("Yes I'm NOT paused! so adding next");
                    audio.play();

                }
                else {
                    console.log("Yes AM paused! NOT playing next");
                    audio.pause();
                }
            }
            else if (i === songList.length) {
                console.log("Finished! reseting highlighted and that Paused now");
                setPause(true);
                setTrope(-1);
            }
            else if (paused == true){
                console.log("I am currently paused so not playing new audio");

            }

          }, true);

            audio.volume = 0.3;
            // audio.loop = false;
            setTrope(0);
            audio.src = songList[0].sound;
            audio.playbackRate = 2;

            // paused ? audio.play() : audio.pause();
            // paused ? console.log('plaay') : console.log('pauase');


            audio.play();
    }
    // const [filter, filtered] = setState(event.target.value)
    let items = flashcards;

    const [quiz, reloadQuiz] = useState(createQuiz(items, getRandomInt(2, 10)));
    const [trope, setTrope] = React.useState(0);

    //pick a song
    // const handleSong = (event, newTrope) => {
    //     setTrope(newTrope);
    // };

    const [value, setValue] = React.useState(10);
    const handleChange = (event, newValue) => {
        setValue(newValue);
        reloadQuiz(createQuiz(items, newValue))
    };

    function handlePlay(){
        setPause(false); 
        makeSongList(audio, quiz);
        console.log("hit play");
    }

    function handlePause(){
        console.log("i'm in handlePause and i'm pausing YOUUUUUUU");
        setPause(true); 
        console.log("hit pause");
    }
    // useEffect(() => {
    //     console.log("effect triggered");
    //     paused ? console.log("effect play is TRUE") : console.log("effect play is FALSE");
    //     console.log("leaving effect trigger");
    //     // const audio = document.getElementById("audio");
    //     // paused ? audio.play() : audio.pause();
    //     // paused ? console.log("effect hi") : console.log("effect bye");

    // })





    // var quiz = createQuiz(items, 4);
    // if (filter){
    //     items = items.filter( item =>
    //         item.id.toLowerCase()
    //         .includes.this.state.filter.toLowerCase)
    // }

    // editSearchTerm = (e) => {
    //     this.setState({searchTerm: e.target.value})
    //   }

      // reloadQuiz = (e) => {
      //   this.setState({quiz: createQuiz(items, 4)})
      // }
    // dynamicSearch = () => {
    //     return flashcards.filter(name => name.toLowerCase().includes(this.state.searchTerm.toLowerCase()))
    //   }


    return (
         <quiz>
            <h5> Practice
           {/* {pause === true ? 'paused' : 'paused'} */}

           <Button size="small" variant="contained"> 
           <Slider value={value} onChange={handleChange} defaultValue={4}
                // position="static"
                step={1} marks min={1} max={8} valueLabelDisplay="auto" />

            </Button>


            <div className="controls">

                <Button variant="contained" color="primary"
                    endIcon={<CachedIcon  onClick={() =>  {reloadQuiz(createQuiz(items, value));  console.log("ReOrder");}}/>}>
                    {/* endIcon={<CachedIcon  onClick={() =>  {reloadQuiz(createQuiz(items, value)); setPause(true); console.log("hit play");}}/>}> */}
                </Button> 

                {paused ?  

                    <Button variant="contained" color="primary" 
                        endIcon={<PlayCircleFilled onClick={() => {handlePlay()}}/>}>
                        {paused ? 'play' : 'pause'}
                    </Button> :
                    <Button variant="contained" color="primary" 
                        endIcon={<PauseCircleFilled onClick={() => {handlePause()}}/>}>
                        {paused ? 'play' : 'pause'}
                    </Button>
                }

                {/* <Bar curTime={curTime} duration={duration} onTimeUpdate={(time) => setClickedTime(time)}/> */}
            </div>
           {/* <Button variant="contained" color="primary"
                endIcon={<CachedIcon  onClick={() =>  {reloadQuiz(createQuiz(items, value)); setPause(true);}}/>}>
            </Button> */}


            {/* <Button variant="contained" color="primary" 
                endIcon={<PlayCircleFilled onClick={() => {makeSongList(audio, quiz);  setPause(false);}}/>}>
            </Button> */}
{/*             
            <Button variant="contained" color="primary" 
                endIcon={<PauseCircleFilled onClick={() => { setPause(true);}}/>}>
            </Button> */}

            </h5>


        <p>
        {quiz.map((songName, index)=> {return ( 
           <i style={(trope === index) ? {color: 'black'} : {color: 'yellow'}}>{songName.heb}</i>)})}
        </p>


           </quiz>

    )
}
