import React from 'react'
import './images/'

export default function Flashcard( {flashcard }) {
    return (
        <div>
            <div className = "front">
                {flashcard.question}
                {flashcard.Image}
                <img src={require(flashcard.image)}></img>
                {/* {flashcard.Image} alt="sd"/>; */}
                <div className = "card-options">
                    {
                        flashcard.options.map(option => {
                            return <div className="flashcard-option">{option}</div>
                        })
                    }
                </div>
            </div>
            <div className = "back">
                {flashcard.answer}
            </div>
        </div>
    )
}
